class PromotionItemsModel {
  int statusCode;
  List<Message> message;
  String messageCode;

  PromotionItemsModel.fromJson(dynamic json) {
    statusCode = json['statusCode'];
    if (json['message'] != null) {
      message = [];
      json['message'].forEach((v) {
        message.add(Message.fromJson(v));
      });
    }
    messageCode = json['messageCode'];
  }

}

class Message {
  String id;
  String name;
  dynamic description;
  String foodCategoryId;
  String price;
  String price2;
  String image;
  // bool haveChilds;
  // bool haveOffers;
  List<AddOnItems> addOnItems; // addOnItems
  // List<OfferItems> offerItems;

  Message.fromJson(dynamic json) {
    id = json['foodItemId'].toString();
    name = appConfig.cApp.appLocale == Locale("en")
        ? json['nameEn']
        : json['nameAr'];
    description = appConfig.cApp.appLocale == Locale("en")
        ? json['descriptionEn']
        : json['descriptionAr'];
    foodCategoryId = json['foodCategoryId'].toString();
    price = json['price'].toString();
    price2 = json['price2'].toString();
    image = json['image'];
    // haveChilds = json['haveChilds'];
    // haveOffers = json['haveOffers'];
    if (json['subItems'] != null) {
      addOnItems = [];
      json['subItems'].forEach((v) {
        addOnItems.add(AddOnItems.fromJson(v));
      });
    }
    // if (json['offerItems'] != null) {
    //   offerItems = [];
    //   json['offerItems'].forEach((v) {
    //     offerItems.add(OfferItems.fromJson(v));
    //   });
    // }
  }

}

