  }

  String id;
  String name;
  dynamic description;
  String foodCategoryId;
  String price;
  String price2;
  String image;
  bool haveChilds;
  bool haveOffers;
  dynamic subItems;
  dynamic offerItems;

  Map<String, dynamic> toJson() {
    final map = <String, dynamic>{};
    map['id'] = id;
    map['name'] = name;
    map['description'] = description;
    map['foodCategoryId'] = foodCategoryId;
    map['price'] = price;
    map['price2'] = price2;
    map['image'] = image;
    map['haveChilds'] = haveChilds;
    map['haveOffers'] = haveOffers;
    map['subItems'] = subItems;
    map['offerItems'] = offerItems;
    return map;
  }
